import { Component, Inject } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { AuthorizeService } from '../../api-authorization/authorize.service';
import { FormBuilder, FormGroup, FormArray, Validators } from '@angular/forms';
import { Recipe } from '../models/Recipe';
import { Ingredient } from '../models/Ingredient';

@Component({
  selector: 'app-add-recipes',
  templateUrl: './add-recipes.component.html',
  styleUrls: ['./add-recipes.component.css']
})
export class AddRecipesComponent {
  recipeForm: FormGroup;
  recipeData: Recipe = <Recipe>{};
  ingredientsData: Ingredient[] = [];

  constructor(
    private http: HttpClient,
    @Inject('BASE_URL') private baseUrl: string,
    private authorize: AuthorizeService,
    private formBuilder: FormBuilder
  ) {
    this.recipeForm = this.formBuilder.group({
      id: [''],
      userid: [''],
      name: ['', Validators.required],
      category: ['', Validators.required],
      difficulty: ['', Validators.required],
      duration: ['', Validators.required],
      description: ['', Validators.required],
      ingredients: this.formBuilder.array([]),
    });

    authorize.getUser().subscribe((result: any) => {
      this.recipeForm.get('userid')?.setValue(result.sub);
    });
  }

  submitForm() {
    if (this.recipeForm.valid) {
      this.http.post<Recipe>(`${this.baseUrl}api/recipes`, this.recipeForm.value).subscribe({
        next: (response) => {
          this.ingredientsArray.controls.forEach(async (value) => {
            value.get('recipeid')?.setValue(response.id);
            await this.http.post<Ingredient>(`${this.baseUrl}api/ingredients`, value.value).toPromise();
          });
          alert("Receita criada com sucesso");
        },
        error: (response) => {
          console.error(response);
        }
      });
    } else {
      alert("Por favor, preencha todos os campos corretamente.");
    }
  }

  get ingredientsArray() {
    return this.recipeForm.get('ingredients') as FormArray;
  }

  addIngredient() {
    this.ingredientsArray.push(
      this.formBuilder.group({
        name: ['', Validators.required],
        quantity: ['', Validators.required],
        recipeid: [''],
      })
    );
  }

  removeIngredient(index: number) {
    this.ingredientsArray.removeAt(index);
  }
}


<form [formGroup]="recipeForm" (ngSubmit)="submitForm()" class="container mt-5">

  <input type="hidden" formControlName="id" />
  <input type="hidden" formControlName="userid" />

  <div class="form-group">
    <label for="name">Nome:</label>
    <input type="text" class="form-control" id="name" formControlName="name" />
  </div>

  <div class="form-row d-flex">
    <div class="form-group col-md-4">
      <label for="category">Categoria:</label>
      <select class="form-control" id="category" formControlName="category">
        <option value="Aperitivo">Aperitivo</option>
        <option value="Carne">Carne</option>
        <option value="Peixe">Peixe</option>
        <option value="Sobremesa">Sobremesa</option>
      </select>
    </div>

    <div class="form-group col-md-4 mx-2">
      <label for="difficulty">Dificuldade:</label>
      <select class="form-control" id="difficulty" formControlName="difficulty">
        <option value="Fácil">Fácil</option>
        <option value="Normal">Normal</option>
        <option value="Difícil">Difícil</option>
      </select>
    </div>

    <div class="form-group col-md-4">
      <label for="duration">Duração:</label>
      <input type="number" class="form-control" id="duration" formControlName="duration" />
    </div>
  </div>

  <div class="form-group">
    <label for="description">Descrição:</label>
    <input type="text" class="form-control" id="description" formControlName="description" />
  </div>

  <div formArrayName="ingredients">
    <div *ngFor="let ingredient of ingredientsArray.controls; let i = index">
      <div [formGroupName]="i" class="row mb-3">
        <div class="col-md-5">
          <label for="ingredientName{{i}}">Nome do Ingrediente:</label>
          <input type="text" class="form-control" id="ingredientName{{i}}" formControlName="name" />
        </div>
        <div class="col-md-5">
          <label for="ingredientQuantity{{i}}">Quantidade:</label>
          <input type="text" class="form-control" id="ingredientQuantity{{i}}" formControlName="quantity" />
        </div>
        <div class="col-md-2 align-self-end">
          <button type="button" class="btn btn-danger" (click)="removeIngredient(i)">Remover</button>
        </div>
      </div>
    </div>
  </div>

  <div class="form-group">
    <button type="button" class="btn btn-success" (click)="addIngredient()">Adicionar Ingredientes</button>
  </div>

  <div class="mb-3"></div>
  <button type="submit" class="btn btn-primary">Guardar</button>
</form>
